import React from "react";
const Hero = () => (
<section className="h-screen flex flex-col justify-center items-center bg-gray-900 text-white">
<h1 className="text-5xl font-bold mb-4">Hi, I'm Ayush</h1>
<p className="text-xl mb-6 text-center max-w-xl">
I am a passionate developer building modern web apps and cool projects.
</p>
<a href="#projects" className="px-6 py-3 bg-yellow-500 text-gray-900 font-bold rounded-lg hover:bg-yellow-600 transition">
See My Work
</a>
</section>
);
export default Hero;
