import React from "react";

const projectData = [
  {
    title: "Project One",
    description: "Description of project one.",
    link: "https://github.com/ayush-7-shukla/project-one",
  },
  {
    title: "Project Two",
    description: "Description of project two.",
    link: "https://github.com/ayush-7-shukla/project-two",
  },
];

const Projects = () => {
  return (
    <section id="projects" className="p-10 bg-gray-900 text-white min-h-screen">
      <h2 className="text-4xl font-bold mb-6">Projects</h2>
      <div className="grid md:grid-cols-2 gap-6">
        {projectData.map((proj, idx) => (
          <div key={idx} className="bg-gray-800 p-6 rounded-lg shadow-lg hover:scale-105 transform transition">
            <h3 className="text-2xl font-bold mb-2">{proj.title}</h3>
            <p className="mb-4">{proj.description}</p>
            <a href={proj.link} target="_blank" className="text-yellow-400 hover:underline">
              View Project
            </a>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Projects;
